(1, 2, '_wp_page_template', 'default'),
(2, 3, '_wp_page_template', 'default'),
(7, 8, '_form', '<div class=\"solicitudInfo\">\n    <h3>Solicita más información</h3>\n    <div class=\"inputsFields\">\n        [text* nombre placeholder \"Nombre*\"]\n        [tel* telefono placeholder \"Teléfono*\"]\n        [email* correo placeholder \"Email*\"]\n        <div class=\"policy-checkbox\">\n            [checkbox* politica-privacidad] \n            <p>He leído y Acepto las <a href=\"#\">condiciones Generales</a> y la <a href=\"#\">Política de privacidad</a></p>\n        </div>\n    </div>\n    <div class=\"submit-button\">\n        [submit \"Enviar\"]\n    </div>\n</div>'),
(8, 8, '_mail', 'a:9:{s:6:\"active\";b:1;s:7:\"subject\";s:25:\"cruzroja \"[your-subject]\"\";s:6:\"sender\";s:34:\"[your-name] <mmascunano@hexer.dev>\";s:9:\"recipient\";s:20:\"mmascunano@hexer.dev\";s:4:\"body\";s:193:\"De: [your-name] <[your-email]>\nAsunto: [your-subject]\n\nCuerpo del mensaje:\n[your-message]\n\n-- \nEste mensaje se ha enviado desde un formulario de contacto en cruzroja (http://localhost/cruzroja)\";s:18:\"additional_headers\";s:0:\"\";s:11:\"attachments\";s:0:\"\";s:8:\"use_html\";b:0;s:13:\"exclude_blank\";b:0;}'),
